function onLoad() {

    //     var sys_id = getParameterValue('sysparm_parent_sys_id');
//     var sys_id = "ec06ef8697311110621cb4b3f153af77";
	var sys_id = getParameterValue('sysparam_id');
    var ga = new GlideAjax('PopulateClientField');
    ga.addParam('sysparm_name', 'getFields');
    ga.addParam('sysparm_sys_id', sys_id);
    ga.getXML(setFieldValues);

    function setFieldValues(response) {
        var data = response.responseXML.documentElement.getAttribute("answer");
        data = JSON.parse(data);
		alert(data.number);
		alert(data.parent.member.user_id);
        g_form.setValue('number', data.number );
		g_form.setValue('member', data.parent.member.user_id,data.parent.member.user_name );
		g_form.setValue('leader', data.parent.leader.user_id,data.parent.leader.user_name );
    }
}
function getParameterValue(name) {
    name = name.replace(/[\[]/, "\\\[").replace(/[\]]/, "\\\]");
    var regexS = "[\\?&]" + name + "=([^&#]*)";
    var regex = new RegExp(regexS);
    var results = regex.exec(this.location);
    if (results == null) {
        return "";
    } else {
        return unescape(results[1]);
    }
}


