sp_widget.client_script:
function($scope, spAriaUtil) {
	$scope.accessibilityModeEnabled = spAriaUtil.isAccessibilityEnabled();
	/* widget controller */
	var c = this;

	if (c.data.sales == '幅内' && c.data.customer == '幅外') {
		if (c.data.title == '価格レビュー依頼（売上配分幅内／客先提示幅外）' ||
			  c.data.title == '価格レビュー依頼【売上配分：BU/事業部ルール】' ||
			  c.data.title == '価格レビュー依頼【売上配分：BU-CFO（商談主管）】' ||
			  c.data.title == '価格レビュー依頼【売上配分：CO-CFO（制度主管）】' ||
			  c.data.title == '価格レビュー依頼【客先提示：BU-CFO（商談主管）】' ||
			  c.data.title == '価格レビュー依頼【売上配分：商談リーダ上長】'){
			  if (c.options.title == '売上配分幅内／客先提示幅外') {
				    c.options.title = c.data.title;
				    c.data.show = true;
			  }
		}else {
			  if (c.data.title.startsWith("価格レビュー依頼【") &&
				    c.options.title == '価格レビュー依頼【売上配分幅内／客先提示幅外】'){
				    c.options.title = c.data.title;
				    c.data.show = true;
			  }  
		}

	}else if (c.data.sales == '幅外' && c.data.customer == '幅外') {
		if (c.data.title == '価格レビュー依頼（売上配分幅外／客先提示幅外）' ||
			  c.data.title == '価格レビュー依頼【売上配分：商談リーダ上長】' ||
			  c.data.title == '価格レビュー依頼【売上配分：BU-CFO（商談主管）】' ||
			  c.data.title == '価格レビュー依頼【売上配分：CO-CFO（制度主管）】' ||
			  c.data.title == '価格レビュー依頼【客先提示：BU-CFO（商談主管）】' ){
			  if (c.options.title == '売上配分幅外／客先提示幅外') {
				    c.options.title = c.data.title;
				    c.data.show = true;
			  }
		}else {
		  	if (c.data.title.startsWith("価格レビュー依頼【") &&
				    c.options.title == '価格レビュー依頼【売上配分幅外／客先提示幅外】'){
				    c.options.title = c.data.title;
				    c.data.show = true;
			  }
		}

	}

	if (c.options.title == c.data.title) {
		c.data.show = true;
	}else {
		c.data.show = false;
	}

}

