sp_widget.script:
(function(){
	var gr = $sp.getInstanceRecord();


	var name = 'sys_id';
	var regexS = "[\\?&]" + name + "=([^&#]*)";
	var regex = new RegExp(regexS);
	var results = regex.exec(gs.action.getGlideURI().toString());
	if (results == null) {
		data.href = $sp.getMenuHREF(gr);
	} else {
		data.href = $sp.getMenuHREF(gr) + "&sysparam_id=" + unescape(results[1]);
	}
	data.target = options.target || "";

	var targeRec = new GlideRecord("x_snc_createnotes_mychild");
  targeRec.get(unescape(results[1]));
	data.title = targeRec.short_description + '';
	data.sales = targeRec.u_sales_distribution + '';
	data.customer = targeRec.u_customer_presentation + '';

})();
