sys_script_include.script:
var PopulateClientField = Class.create();
PopulateClientField.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getFields: function() {
        var return_rec = {};
        var childRecord = new GlideRecord("x_snc_createnotes_mychild");
        childRecord.get(this.getParameter('sysparm_sys_id'));
        return_rec.number = childRecord.number + '';
        return_rec.parent = {};
        return_rec.parent.member = {};
        return_rec.parent.member.user_id = childRecord.parent.member.user_name + '';
        return_rec.parent.member.user_name = childRecord.parent.member.name + '';
		return_rec.parent.leader = {};
		return_rec.parent.leader.user_id = childRecord.parent.leader.user_name + '';
		return_rec.parent.leader.user_name = childRecord.parent.leader.name + '';
        return JSON.stringify(return_rec);
    },
    type: 'PopulateClientField'
});
